import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaTrashAlt, FaEdit, FaCheckCircle } from 'react-icons/fa';
import EditTask from './EditTask'; // Import the EditTask component

const TaskItem = () => {
  const [tasks, setTasks] = useState([]);
  const [filters, setFilters] = useState({
    priority: '',
    dueDate: '',
    completed: ''
  });
  const [editTask, setEditTask] = useState(null); // Track task to edit

  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/tasks/tasks')
      .then(response => setTasks(response.data))
      .catch(error => console.error('Error fetching tasks:', error));
  }, []);

  const handleComplete = (taskId) => {
    axios.patch(`http://127.0.0.1:8000/api/tasks/${taskId}/mark_completed/`)
      .then(() => setTasks(tasks.map(task => task.id === taskId ? { ...task, completed: true } : task)))
      .catch(error => console.error('Error marking task as complete:', error));
  };

  const handleDelete = (taskId) => {
    axios.delete(`http://127.0.0.1:8000/api/tasks/${taskId}/`)
      .then(() => setTasks(tasks.filter(task => task.id !== taskId)))
      .catch(error => console.error('Error deleting task:', error));
  };

  const handleEdit = (task) => {
    setEditTask(task); // Set the task to be edited
  };

  const handleSave = (updatedTask) => {
    setTasks(tasks.map(task => (task.id === updatedTask.id ? updatedTask : task)));
    setEditTask(null); // Close the modal
  };

  const filteredTasks = tasks.filter(task =>
    (filters.priority === '' || task.priority === filters.priority) &&
    (filters.dueDate === '' || task.due_date === filters.dueDate) &&
    (filters.completed === '' || task.completed.toString() === filters.completed)
  );

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-semibold mb-4">Task List</h2>
      {/* Filters */}
      <div className="mb-4 flex space-x-4">
        <select
          name="priority"
          className="form-select w-1/3"
          onChange={(e) => setFilters({ ...filters, priority: e.target.value })}
          value={filters.priority}
        >
          <option value="">All Priorities</option>
          <option value="low">Low</option>
          <option value="medium">Medium</option>
          <option value="high">High</option>
        </select>
        <input
          type="date"
          name="dueDate"
          className="form-input w-1/3"
          onChange={(e) => setFilters({ ...filters, dueDate: e.target.value })}
          value={filters.dueDate}
        />
        <select
          name="completed"
          className="form-select w-1/3"
          onChange={(e) => setFilters({ ...filters, completed: e.target.value })}
          value={filters.completed}
        >
          <option value="">All Statuses</option>
          <option value="true">Completed</option>
          <option value="false">Incomplete</option>
        </select>
      </div>
      {/* Task List */}
      <ul className="space-y-4">
        {filteredTasks.map(task => (
          <li key={task.id} className="bg-white p-4 rounded-lg shadow-md flex justify-between items-center">
            <div>
              <h3 className="text-lg font-semibold">{task.title}</h3>
              <p>{task.description}</p>
              <p>Due Date: {task.due_date}</p>
              <p>Priority: {task.priority}</p>
              <p>Status: {task.completed ? 'Completed' : 'Incomplete'}</p>
            </div>
            <div className="flex space-x-3">
              {!task.completed && (
                <button onClick={() => handleComplete(task.id)} className="bg-green-500 text-white px-4 py-2 rounded-lg">
                  <FaCheckCircle /> Mark Complete
                </button>
              )}
              <button onClick={() => handleEdit(task)} className="bg-blue-500 text-white px-4 py-2 rounded-lg">
                <FaEdit /> Edit
              </button>
              <button onClick={() => handleDelete(task.id)} className="bg-red-500 text-white px-4 py-2 rounded-lg">
                <FaTrashAlt /> Delete
              </button>
            </div>
          </li>
        ))}
      </ul>
      {/* Edit Task Modal */}
      {editTask && <EditTask task={editTask} onSave={handleSave} onClose={() => setEditTask(null)} />}
    </div>
  );
};

export default TaskItem;
