import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TaskItem from './TaskItem'; // Import TaskItem component

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [filters, setFilters] = useState({
    priority: '',
    dueDate: '',
    completed: ''
  });

  // Fetch tasks from API
  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/tasks')
      .then(response => {
        setTasks(response.data);
      })
      .catch(error => {
        console.error('Error fetching tasks:', error);
      });
  }, []);

  const handleComplete = (taskId) => {
    axios.patch(`http://127.0.0.1:8000/api/tasks/${taskId}/complete`)
      .then(response => {
        setTasks(tasks.map(task => task.id === taskId ? { ...task, completed: true } : task));
      })
      .catch(error => {
        console.error('Error marking task as complete:', error);
      });
  };

  const handleDelete = (taskId) => {
    axios.delete(`http://127.0.0.1:8000/api/tasks/${taskId}`)
      .then(response => {
        setTasks(tasks.filter(task => task.id !== taskId));
      })
      .catch(error => {
        console.error('Error deleting task:', error);
      });
  };

  const handleEdit = (task) => {
    // Add functionality to edit task
    console.log('Editing task:', task);
  };

  const handleFilterChange = (e) => {
    setFilters({ ...filters, [e.target.name]: e.target.value });
  };

  const filteredTasks = tasks.filter(task => {
    return (
      (filters.priority === '' || task.priority === filters.priority) &&
      (filters.dueDate === '' || task.due_date === filters.dueDate) &&
      (filters.completed === '' || task.completed.toString() === filters.completed)
    );
  });

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-semibold mb-4">Task List</h2>

      {/* Filters */}
      <div className="mb-4 flex space-x-4">
        <select
          name="priority"
          className="form-select w-1/3"
          onChange={handleFilterChange}
          value={filters.priority}
        >
          <option value="">All Priorities</option>
          <option value="low">Low</option>
          <option value="medium">Medium</option>
          <option value="high">High</option>
        </select>

        <input
          type="date"
          name="dueDate"
          className="form-input w-1/3"
          onChange={handleFilterChange}
          value={filters.dueDate}
        />

        <select
          name="completed"
          className="form-select w-1/3"
          onChange={handleFilterChange}
          value={filters.completed}
        >
          <option value="">All Statuses</option>
          <option value="true">Completed</option>
          <option value="false">Incomplete</option>
        </select>
      </div>

      {/* Task List */}
      <ul className="space-y-4">
        {filteredTasks.map((task) => (
          <TaskItem
            key={task.id}
            task={task}
            onComplete={handleComplete}
            onDelete={handleDelete}
            onEdit={handleEdit}
          />
        ))}
      </ul>
    </div>
  );
};

export default TaskList;
