import React from 'react';
import { FaTrashAlt, FaEdit, FaCheckCircle } from 'react-icons/fa'; // Bootstrap icons for actions

const TaskItem = ({ task, onComplete, onDelete, onEdit }) => {
  return (
    <li className="bg-white p-4 rounded-lg shadow-md flex justify-between items-center">
      <div className="flex flex-col">
        <h3 className="text-lg font-semibold">{task.title}</h3>
        <p className="text-gray-600">{task.description}</p>
        <p className="text-sm text-gray-500">Due Date: {task.due_date}</p>
        <p className="text-sm text-gray-500">Priority: {task.priority}</p>
        <p className="text-sm text-gray-500">
          Status: {task.completed ? <span className="text-green-500">Completed</span> : <span className="text-red-500">Incomplete</span>}
        </p>
      </div>
      <div className="flex space-x-3">
        {!task.completed && (
          <button
            onClick={() => onComplete(task.id)}
            className="px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600"
          >
            <FaCheckCircle />
            Mark as Complete
          </button>
        )}

        <button
          onClick={() => onEdit(task)}
          className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
        >
          <FaEdit />
          Edit
        </button>

        <button
          onClick={() => onDelete(task.id)}
          className="px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600"
        >
          <FaTrashAlt />
          Delete
        </button>
      </div>
    </li>
  );
};

export default TaskItem;
