import React, { useState, useEffect } from 'react';

function TaskList() {
  const [tasks, setTasks] = useState([]);  // Initial state set to an empty array
  const [loading, setLoading] = useState(true);  // To handle loading state
  const [error, setError] = useState(null);     // To handle any errors

  useEffect(() => {
    // Fetch tasks from the API
    fetch('http://127.0.0.1:8000/api/taskstasks/')
      .then((response) => response.json())
      .then((data) => {
        // Check if the 'tasks' field is present and is an array
        if (data && data.tasks && Array.isArray(data.tasks)) {
          setTasks(data.tasks);
        } else {
          console.error('Expected an array of tasks, but got:', data);
          setTasks([]);  // Set an empty array if data format is incorrect
        }
        setLoading(false);  // Set loading to false after data is fetched
      })
      .catch((error) => {
        console.error('Error fetching tasks:', error);
        setError(error.message);  // Store error message if the fetch fails
        setLoading(false);        // Set loading to false even if there's an error
      });
  }, []);  // Empty dependency array to fetch data once on mount

  // If data is still loading
  if (loading) {
    return <div>Loading tasks...</div>;
  }

  // If there was an error fetching the tasks
  if (error) {
    return <div>Error: {error}</div>;
  }

  // Render the tasks list
  return (
    <div>
      {tasks.length === 0 ? (
        <p>No tasks available</p>
      ) : (
        tasks.map((task) => (
          <div key={task.id}>
            <h3>{task.title}</h3>
            <p>{task.description}</p>
          </div>
        ))
      )}
    </div>
  );
}

export default TaskList;
