// api.js
import axios from 'axios';

export async function fetchTasks() {
    try {
        const response = await axios.get('http://localhost:8000/api/tasks/');
        return response.data;
    } catch (error) {
        console.error('Error fetching tasks:', error);
        throw error;
    }
}
