// App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

import Home from './Home';
import About from './About';

const App = () => {
  return (
    <Router>
      <Routes>
       <Route path="/" exact>
<TaskList />
</Route>
<Route path="/create" exact>
<TaskForm onTaskCreated={() => window.location.href = "/"} />
</Route>
      </Routes>
    </Router>
  );
};

export default App;
