import React, { useEffect, useState } from 'react';
import { fetchTasks } from './s';
import TaskForm from './components/TaskForm';
import TaskList from './components/TaskList';

function App() {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch tasks on mount
  useEffect(() => {
    const getTasks = async () => {
      try {
        const taskData = await fetchTasks();
        setTasks(taskData);
        setLoading(false);
      } catch (err) {
        setError('Error fetching tasks');
        setLoading(false);
      }
    };

    getTasks();
  }, []);

  return (
    <div className="max-w-7xl mx-auto p-6">
      <h1 className="text-4xl text-center font-semibold text-blue-600">Task Manager</h1>
      <TaskForm setTasks={setTasks} />
      {loading ? <div className="text-center text-xl">Loading...</div> : <TaskList tasks={tasks} />}
      {error && <div className="text-center text-xl text-red-500">{error}</div>}
    </div>
  );
}

export default App;
