import React from 'react';
import { toggleTaskCompletion, deleteTask } from '../api/api';

const TaskItem = ({ task }) => {
  const { id, title, description, start_date, end_date, completed } = task;

  const handleToggleCompletion = async () => {
    const updatedTask = await toggleTaskCompletion(id);
    console.log('Updated task:', updatedTask);
  };

  const handleDelete = async () => {
    await deleteTask(id);
    console.log('Task deleted');
  };

  return (
    <li className="task-item">
      <h3>{title}</h3>
      <p>{description}</p>
      <p>
        <strong>Start Date:</strong> {start_date} <br />
        <strong>End Date:</strong> {end_date}
      </p>
      <button onClick={handleToggleCompletion}>
        {completed ? 'Mark as Incomplete' : 'Mark as Completed'}
      </button>
      <button onClick={handleDelete} className="delete-btn">Delete</button>
    </li>
  );
};

export default TaskItem;
