import React, { useState } from 'react';
import { createTask } from '../services/api';

function TaskForm({ refreshTasks }) {
  const [taskData, setTaskData] = useState({
    title: '',
    description: '',
    endDate: '',
    priority: 'medium'
  });

  const handleChange = (e) => {
    setTaskData({ ...taskData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createTask(taskData);
      setTaskData({ title: '', description: '', endDate: '', priority: 'medium' });
      refreshTasks();
    } catch (error) {
      console.error("Error creating task:", error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow-lg mb-6">
      <h3 className="text-xl font-semibold mb-4">Create New Task</h3>

      <input
        type="text"
        name="title"
        placeholder="Task Title"
        value={taskData.title}
        onChange={handleChange}
        className="w-full mb-3 p-2 border border-gray-300 rounded-lg"
        required
      />
      <textarea
        name="description"
        placeholder="Task Description"
        value={taskData.description}
        onChange={handleChange}
        className="w-full mb-3 p-2 border border-gray-300 rounded-lg"
      ></textarea>
      <input
        type="date"
        name="endDate"
        value={taskData.endDate}
        onChange={handleChange}
        className="w-full mb-3 p-2 border border-gray-300 rounded-lg"
        required
      />
      <div className="mb-4">
        <label className="block text-gray-700">Priority</label>
        <select
          name="priority"
          value={taskData.priority}
          onChange={handleChange}
          className="w-full p-2 border border-gray-300 rounded-lg"
        >
          <option value="high">High</option>
          <option value="medium">Medium</option>
          <option value="low">Low</option>
        </select>
      </div>
      <button
        type="submit"
        className="w-full py-2 bg-blue-500 text-white font-semibold rounded-lg hover:bg-blue-600"
      >
        Add Task
      </button>
    </form>
  );
}

export default TaskForm;
