import React, { useEffect, useState } from 'react';
import { getTasks, deleteTask, markTaskCompleted } from '../services/api';
import TaskItem from './TaskItem';

function TaskList() {
  const [tasks, setTasks] = useState([]);
  const [filter, setFilter] = useState({ priority: '', completed: '', dueDate: '' });

  const fetchTasks = async () => {
    const data = await getTasks(filter);
    setTasks(data);
  };

  useEffect(() => {
    fetchTasks();
  }, [filter]);

  const handleDelete = async (taskId) => {
    await deleteTask(taskId);
    fetchTasks();
  };

  const handleComplete = async (taskId) => {
    await markTaskCompleted(taskId);
    fetchTasks();
  };

  const handleFilterChange = (e) => {
    setFilter({ ...filter, [e.target.name]: e.target.value });
  };

  return (
    <div>
      <h2>Task List</h2>
      <div>
        <label>Priority:</label>
        <select name="priority" onChange={handleFilterChange}>
          <option value="">All</option>
          <option value="high">High</option>
          <option value="medium">Medium</option>
          <option value="low">Low</option>
        </select>
        <label>Status:</label>
        <select name="completed" onChange={handleFilterChange}>
          <option value="">All</option>
          <option value="true">Completed</option>
          <option value="false">Incomplete</option>
        </select>
        <label>Due Date:</label>
        <input type="date" name="dueDate" onChange={handleFilterChange} />
      </div>

      <ul>
        {tasks.map((task) => (
          <TaskItem
            key={task.id}
            task={task}
            onDelete={() => handleDelete(task.id)}
            onComplete={() => handleComplete(task.id)}
          />
        ))}
      </ul>
    </div>
  );
}

export default TaskList;
