import React, { useState } from 'react';

function TaskForm({ setTasks }) {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [priority, setPriority] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [completed, setCompleted] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const newTask = { title, description, priority, dueDate, completed };

    try {
      const response = await fetch('http://127.0.0.1:8000/tasks/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newTask),
      });

      if (response.ok) {
        const addedTask = await response.json();
        setTasks((prevTasks) => [...prevTasks, addedTask]);
        setTitle('');
        setDescription('');
        setPriority('');
        setDueDate('');
        setCompleted(false);
      } else {
        alert('Failed to add task');
      }
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="max-w-2xl mx-auto my-4 p-6 bg-white shadow-lg rounded-lg">
      <h2 className="text-2xl mb-4">Add New Task</h2>
      <form onSubmit={handleSubmit}>
        <input
          className="w-full p-2 mb-4 border border-gray-300 rounded-md"
          type="text"
          placeholder="Task Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <textarea
          className="w-full p-2 mb-4 border border-gray-300 rounded-md"
          placeholder="Task Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        ></textarea>
        <input
          className="w-full p-2 mb-4 border border-gray-300 rounded-md"
          type="text"
          placeholder="Priority"
          value={priority}
          onChange={(e) => setPriority(e.target.value)}
        />
        <input
          className="w-full p-2 mb-4 border border-gray-300 rounded-md"
          type="date"
          value={dueDate}
          onChange={(e) => setDueDate(e.target.value)}
        />
        <div className="mb-4">
          <label className="text-gray-700">Completed:</label>
          <input
            type="checkbox"
            checked={completed}
            onChange={() => setCompleted(!completed)}
            className="ml-2"
          />
        </div>
        <button
          type="submit"
          className="w-full py-2 px-4 bg-blue-600 text-white rounded-md hover:bg-blue-700"
        >
          Add Task
        </button>
      </form>
    </div>
  );
}

export default TaskForm;
