import React from 'react';
import { AiOutlineDelete, AiOutlineCheckCircle } from 'react-icons/ai';

function TaskItem({ task, onDelete, onComplete }) {
  return (
    <li className="bg-white p-5 rounded-lg shadow-lg flex justify-between items-start space-x-4 transition-transform duration-200 hover:scale-105 hover:shadow-xl">
      <div className="flex-1">
        <h3 className="text-xl font-semibold text-gray-800">{task.title}</h3>
        <p className="text-gray-600 mt-1">{task.description}</p>
        <p className="text-sm text-gray-500 mt-2">Due Date: {task.end_date}</p>
        <p className="text-sm text-gray-500">Priority: {task.priority}</p>
        <p className="text-sm text-gray-500">
          Status: <span className={task.completed ? 'text-green-500' : 'text-red-500'}>{task.completed ? "Completed" : "Incomplete"}</span>
        </p>
      </div>
      <div className="flex flex-col space-y-2">
        <button
          onClick={onComplete}
          disabled={task.completed}
          className={`flex items-center justify-center px-3 py-2 rounded-lg text-sm font-medium transition-colors duration-200 ${
            task.completed
              ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
              : 'bg-green-500 text-white hover:bg-green-600'
          }`}
        >
          <AiOutlineCheckCircle className="mr-2" />
          {task.completed ? "Completed" : "Mark as Complete"}
        </button>
        <button
          onClick={onDelete}
          className="flex items-center justify-center px-3 py-2 bg-red-500 text-white rounded-lg text-sm font-medium hover:bg-red-600 transition-colors duration-200"
        >
          <AiOutlineDelete className="mr-2" />
          Delete
        </button>
      </div>
    </li>
  );
}

export default TaskItem;
