import React, { useState, useEffect } from 'react';
import { createTask } from '../services/api';

function TaskForm({ refreshTasks }) {
  // Initial state for task data
  const [taskData, setTaskData] = useState({
    title: '',
    description: '',
    startDate: '',
    endDate: '',
    completed: false
  });

  useEffect(() => {
    // Set today's date as the default for the start date
    const today = new Date().toISOString().split('T')[0];
    setTaskData((prevData) => ({ ...prevData, startDate: today }));
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setTaskData({
      ...taskData,
      [name]: type === 'checkbox' ? checked : value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Ensure end date is after start date
    if (new Date(taskData.startDate) > new Date(taskData.endDate)) {
      alert('End date must be after the start date.');
      return;
    }

    try {
      await createTask(taskData);
      setTaskData({ title: '', description: '', startDate: '', endDate: '', completed: false });
      refreshTasks();
    } catch (error) {
      console.error("Error creating task:", error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="bg-white p-4 rounded shadow-md mb-4">
      {/* Task Title */}
      <input
        type="text"
        name="title"
        placeholder="Task Title"
        value={taskData.title}
        onChange={handleChange}
        required
        className="w-full mb-2 p-2 border rounded"
      />
      
      {/* Task Description */}
      <textarea
        name="description"
        placeholder="Task Description (optional)"
        value={taskData.description}
        onChange={handleChange}
        className="w-full mb-2 p-2 border rounded"
      />

      {/* Start Date */}
      <input
        type="date"
        name="startDate"
        value={taskData.startDate}
        onChange={handleChange}
        required
        className="w-full mb-2 p-2 border rounded"
      />
      
      {/* End Date */}
      <input
        type="date"
        name="endDate"
        value={taskData.endDate}
        onChange={handleChange}
        required
        className="w-full mb-2 p-2 border rounded"
      />

      {/* Completed Checkbox */}
      <label className="flex items-center mb-2">
        <input
          type="checkbox"
          name="completed"
          checked={taskData.completed}
          onChange={handleChange}
          className="mr-2"
        />
        Task Completed
      </label>

      {/* Submit Button */}
      <button type="submit" className="bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600">
        Add Task
      </button>
    </form>
  );
}

export default TaskForm;
